// Base64占位图片
export const placeholderImages = {
  // 背景图片 - 简单渐变
  background: {
    village: 'data:image/svg+xml;base64,',
    forest1: 'data:image/svg+xml;base64,',
    forest2: 'data:image/svg+xml;base64,',
    default: 'data:image/svg+xml;base64,'
  },
  // 角色头像 - 简单圆形
  role: {
    hero: 'data:image/svg+xml;base64,',
    elder: 'data:image/svg+xml;base64,',
    spirit: 'data:image/svg+xml;base64,',
    grandpa: 'data:image/svg+xml;base64,',
    default: 'data:image/svg+xml;base64,'
  }
};

// 获取图片URL，如果文件不存在则返回占位图
export const getImageUrl = (path: string): string => {
  if (!path) {
    return placeholderImages.background.default;
  }

  // 检查是否是assets路径
  if (path.startsWith('assets/backgrounds/')) {
    const filename = path.split('/').pop()?.replace('.png', '');
    if (filename && filename in placeholderImages.background) {
      return placeholderImages.background[filename as keyof typeof placeholderImages.background];
    }
    // 返回默认背景
    return placeholderImages.background.default;
  } else if (path.startsWith('assets/roles/')) {
    const filename = path.split('/').pop()?.replace('.png', '');
    if (filename && filename in placeholderImages.role) {
      return placeholderImages.role[filename as keyof typeof placeholderImages.role];
    }
    // 返回默认角色
    return placeholderImages.role.default;
  }

  // 如果不是assets路径，尝试用文件名匹配
  const filename = path.split('/').pop()?.replace('.png', '');

  // 尝试在背景中查找
  if (filename && filename in placeholderImages.background) {
    return placeholderImages.background[filename as keyof typeof placeholderImages.background];
  }

  // 尝试在角色中查找
  if (filename && filename in placeholderImages.role) {
    return placeholderImages.role[filename as keyof typeof placeholderImages.role];
  }

  // 默认返回原路径（可能是外部URL或其他路径）
  return path;
};
